import React, {Component} from "react";
import Profile from "../components/Profile";
import { NavLink } from 'react-router-dom';
import {Segment, Icon, Rating } from "semantic-ui-react"


class TrailDetails extends Component {
  constructor(props) {
    super();
    this.state= {
      trail:[]
      }
    }

 componentDidMount() {
   fetch('http://localhost:3000/api/v1/trails/')
     .then(response => response.json())
     .then(trailsData => this.setState({
       trail: trailsData.trails.find(trail => trail.id == this.props.location.pathname.split('/')[2])
     }));
   }

   render(){
     return(
       <div size="massive">
         <div style={{textAlign: "center"}}>
            <h2 style={{marginLeft: "25px"}}>{this.state.trail.name}</h2>
         </div>
         <div>
           <div className= "image" >
             <img style={{ borderRadius : "8px", height: "140%", width: "90%", marginLeft: "25px"}} alt="oh no!" src= {this.state.trail.imgMedium}/>
           </div>
           <div>
             <div style={{marginLeft: "25px"}}><strong>Location: </strong> {this.state.trail.location}</div>
           </div>
           <div style={{marginLeft: "25px"}}>
             <span>
               <strong> Difficulty: {this.state.trail.difficulty} </strong>
             </span>
           <div>
             <span>
               <strong>Star Rating: </strong>
               <Rating icon='star' defaultRating={this.state.trail.star} maxRating={5} />
               {this.state.trail.star}
             </span>
           </div>
           </div>
         </div>

       </div>
     )
   }
}


export default TrailDetails;
