import React, { Component } from 'react';
import './App.css';
import NavBar from "./components/NavBar"
import MainContainer from "./containers/MainContainer"
import {Router, Route, Link, Switch, withRouter } from 'react-router-dom';
import Login from "./containers/Login"
import Signup from "./containers/Signup"
import UpdateProfile from "./containers/UpdateProfile"
import About from "./components/About"
import Profile from "./components/Profile"
import TrailDetails from "./components/TrailDetails"


class App extends Component {
  constructor() {
    super()
    this.state = {
      users: [],
      currentUser: '',
      myTrails: []
    }
  }

  // componentDidMount() {
  //   fetch(this.server + "trails")
  //   .then(resp => resp.json())
  //   .then(data => this.setState({
  //     trails: data.trails
  //   })
  //  )
  // }

  // desiredTrails = () => {
  //    let tempTrails = [...this.state.trails]
  //    let filteredTrails = tempTrails.filter(trail => {
  //      if (trail.name.toLowerCase().includes(this.state.search)) {
  //        return trail
  //      }
  //      else if (trail.location.toLowerCase().includes(this.state.search)){
  //        return trail
  //      }
  //      else if (trail.difficulty.includes(this.state.search)){
  //        return trail
  //      }
  //    })
  //    return filteredTrails
  //  }


  componentDidMount(){
    let token = localStorage.getItem("token")

    if(token){
      fetch(`http://localhost:3000/api/v1/current_user`, {
        headers: {
          "Authorization": token
        }
      })
      .then(res => res.json())
      .then((response) => {
        this.setState({
          currentUser: response
        })
      })
    }
  }

  signup = ({name, email, image, location, password, passwordConfirmation}) => {
     // if (password === passwordConfirmation) {
      fetch("http://localhost:3000/api/v1/users", {
				method: "POST",
				headers: {
					'Content-Type': "application/json",
					'Accept': "application/json"
				},
				body: JSON.stringify({
					name: name,
          email: email,
          image: image,
          location: location,
          password: password
				})
			})
			.then(res => res.json())
			.then((response) => {
				if (response.errors){
					alert(response.errors)
				} else {
					localStorage.setItem("token", response.token)
					this.setState({
						currentUser: response.user
					})
          this.props.history.push("/profile")
				}
			 })
    }

    logout = () => {
      this.setState({
        currentUser: null
      })
      localStorage.removeItem("token")
      this.props.history.push("/login")
    }

    login = (email, password) => {
      fetch("http://localhost:3000/api/v1/login", {
        method: "POST",
        headers: {
          'Content-Type': "application/json",
					'Accept': "application/json"
        },
        body: JSON.stringify({
          email: email,
          password: password
        })
      })
      .then(resp => resp.json())
      .then(response => {
        if (response.errors) {
          alert(response.errors)
        } else {
          localStorage.setItem("token", response.token)
          this.setState({
            currentUser: response.user
          })
          this.props.history.push("/profile")
        }
      })
    }



  render() {
    return (
      <div className="App">
        <NavBar currentUser={this.state.currentUser} logout={this.logout}/>
        <Switch>
           <Route exact path="/"  component={About} />
           <Route exact path="/home" render={(props) => <MainContainer /> } />
           <Route exact path="/trail/:id" render={(props) => <TrailDetails {...props} currentUser={this.state.currentUser} users={this.state.users}/> } />
           <Route exact path="/login" render={(props) => <Login {...props}  login={this.login} /> } />
           <Route exact path="/signup" render={(props) => <Signup {...props} signup={this.signup} /> } />
           <Route exact path="/edit" render={(props) => <UpdateProfile {...props} currentUser={this.state.currentUser}/> } />
           <Route exact path="/:username" render={(props) => <Profile {...props} currentUser={this.state.currentUser} logout={this.logout} /> } />
         </Switch>
       </div>
    );
  }
}

export default withRouter(App)
