import React, { Component } from "react"
import HomeScreen from "../components/HomeScreen"
import {Router, Route, Link, Switch } from 'react-router-dom';
import About from "../components/TrailCard"
import Login from "../containers/Login"
import TrailsList from "../containers/TrailsList"
import {Card, Icon, Search, Segment} from "semantic-ui-react"


class MainContainer extends Component {
  constructor() {
    super()
    this.state = {
      trails: [],
      search: ""
    }
    this.server = "http://localhost:3000/api/v1/"
  }

  getSearchInput = (e) => {
    this.setState({
      trails: [...this.state.trails],
      search: e.target.value
    })
  }

   componentDidMount() {
     fetch(this.server + "trails")
     .then(resp => resp.json())
     .then(data => this.setState({
       trails: data.trails
     })
    )
   }

  render() {
    const desiredTrails = this.state.trails.filter(trail => trail.name.includes(this.state.search))
    return(
    <div>I am Main container
      <Segment>
        <Search onChange={this.getSearchInput}/>
      </Segment>
      <TrailsList trails={desiredTrails}/>
    </div>
    )
  }
}
export default MainContainer;
