import React, { Component } from "react"
import TrailCard from "../components/TrailCard"
import {Card, Icon, Search, Segment} from "semantic-ui-react"

class TrailsList extends Component {

  state = {
    search: ""
  }

  getSearchInput = (e, { value }) => {
    this.setState({
      search: value
    })
  }

 render(){
   const desiredTrail = this.props.trails.filter(trail => trail.name.includes(this.state.search))
   console.log(desiredTrail)
    return(
      <div>
       <Segment>
         <Search onChange={this.getSearchInput}/>
       </Segment>
       <Segment>
          <Card.Group itemsPerRow={3}>
            {
              this.props.trails.map(trail => < TrailCard trail= {trail}/> )
            }
          </Card.Group>
        </Segment>
      </div>
    )
  }
}

export default TrailsList;
