import React, {Component} from "react";
import Profile from "../components/Profile";
import { NavLink } from 'react-router-dom';
import {Segment, Icon, Rating, Grid, Form } from "semantic-ui-react"


class TrailDetails extends Component {
  constructor(props) {
    super();
    this.state= {
      trail:[],
      comments:[]
      }
    }

 componentDidMount() {
   fetch('http://localhost:3000/api/v1/trails/')
     .then(response => response.json())
     .then(trailsData => this.setState({
       trail: trailsData.trails.find(trail => trail.id == this.props.location.pathname.split('/')[2])
     }));
   }



   render(){
     return(
       <div>
         <div style={{textAlign: "center"}}>
            <h2 style={{marginLeft: "25px"}}>{this.state.trail.name}</h2>
         </div>
         <div style={{marginLeft: "25px"}}>
            <img style={{ borderRadius : "8px", height: "40%", width: "40%", marginLeft: "25px"}} alt="oh no!" src= {this.state.trail.imgMedium}/>
            <h2><blockquote>"{this.state.trail.summary}"</blockquote></h2>
            <h3>Location: {this.state.trail.location}  </h3>
            <div>
              <h3 >
                Length: {this.state.trail.length} <br></br>
                Ascent:{this.state.trail.ascent} <br></br>
                Descent: {this.state.trail.descent} <br></br>
                Difficulty: {this.state.trail.difficulty} <br></br>
              </h3>
            </div>
            <div>
               <h4>Rating:
                  {this.state.trail.stars} <Rating icon='star' defaultRating={5}/>
               </h4>
            </div>
          </div>
          <div class="conditions">
            <h3 style={{textAlign: "center"}}>Conditions:</h3>
              <ul>
                <li> {this.state.trail.conditionStatus} </li>
                <li> {this.state.trail.conditionDetails} </li>
              </ul>
          </div>
          <div class="comments">
            <h2>Comments</h2>
            <div>
            /* comments */
            </div>
            <Form.Input fluid label="Name" placeholder="Name" name="name" value={this.state.name} onChange={this.handleChange}/>
          </div>
       </div>
     )
   }
}


export default TrailDetails;
