import React, {Component} from "react";
import Profile from "../components/Profile";
import { NavLink } from 'react-router-dom';
import {Segment, Icon, Rating } from "semantic-ui-react"


class TrailDetails extends Component {
  constructor(props) {
    super();
    this.state= {
      trail:[]
      }
    }

 componentDidMount() {
   fetch('http://localhost:3000/api/v1/trails/')
     .then(response => response.json())
     .then(trailsData => this.setState({
       trail: trailsData.trails.find(trail => trail.id == this.props.location.pathname.split('/')[2])
     }));
   }

   render(){
     return(
       <div size="massive">
         <div style={{textAlign: "center"}}>
            <h2 style={{marginLeft: "25px"}}>{this.state.trail.name}</h2>
         </div>
         <div>
            <img style={{ borderRadius : "8px", height: "70%", width: "70%", marginLeft: "25px"}} alt="oh no!" src= {this.state.trail.imgMedium}/>
            <span>"{this.state.trail.summary}"</span>
            <h3 style={{marginLeft: "25px"}}>Location: {this.state.trail.location}  </h3>
            <div style={{marginLeft: "25px"}}>
              <h3>
                Length: {this.state.trail.length} <br></br>
                Difficulty: {this.state.trail.difficulty}
              </h3>
            </div>
            <div style={{marginLeft: "25px"}}>
               <h4>Rating:
                  {this.state.trail.stars} <Rating icon='star' defaultRating={5}/>
               </h4>
            </div>
          </div>
       </div>
     )
   }
}


export default TrailDetails;
