import React, { Component } from "react"
import HomeScreen from "../components/HomeScreen"
import {Router, Route, Link, Switch } from 'react-router-dom';
import About from "../components/TrailCard"
import Login from "../containers/Login"
import TrailsList from "../containers/TrailsList"
import { Search } from "semantic-ui-react"
import { Segment } from "semantic-ui-react"


class MainContainer extends Component {
  constructor() {
    super()
    this.state = {
      trails: [],
      search: ""
    }
    this.server = "http://localhost:3000/api/v1/"
  }

  getSearchInput = (e, {value} ) => {
    this.setState({
      search: value
    })
  }

   componentDidMount() {
     fetch(this.server + "trails")
     .then(resp => resp.json())
     .then(data => this.setState({
       trails: data.trails
     })
    )
   }

  desiredTrails = () => {
     let tempTrails = [...this.state.trails]
     let filteredTrails = tempTrails.filter(trail => {
       if (trail.name.toLowerCase().includes(this.state.search)) {
         return trail
       }
       else if (trail.location.toLowerCase().includes(this.state.search)){
         return trail
       }
       else if (trail.distance.includes(this.state.search)) {
         return trail
       }
     })
     return filteredTrails
   }

  render() {
    return(
    <div>I am Main container
      <Segment>
        <Search onSearchChange={this.getSearchInput} />
      </Segment>
      <TrailsList trails={this.desiredTrails()}/>
    </div>
    )
  }
}
export default MainContainer;
