import React from "react"
import {Card, Icon, Rating, Button } from "semantic-ui-react"
import { NavLink } from 'react-router-dom';
import { style } from "../App.css"


const difficulty = () => {
  let level;
  if (this.state.trail.difficulty === "blue") {
    level = "Blue";
  } else if (this.state.trail.difficulty === "greenBlue") {
    level = "Intermediate Green";
  } else if (this.state.trail.difficulty === "blueBlack") {
    level = "Intermediate Blue";
  } else if (this.state.trail.difficulty === "black") {
    level = "Black ðŸ”·";
  } else if (this.state.trail.difficulty === "dblack") {
    level = "Double Black ðŸ”·";
  } else if (this.state.trail.difficulty === "green") {
    level = "Easy"
  } else {
    level = "N/A"
  }
  return level;
}


const TrailCard = (props) => {
  return (
    <Card style= {{margin: "10px"}}>
      <div style={{textAlign: "center"}}>
        <div className= "image" >
          <img style={{ borderRadius : "8px", height: "140px", width: "100%", padding: "1%"}} alt="oh no!" src= {props.trail.imgSmall}/>
        </div>
        <div className="content">
          <div className="header">
            <h4>{props.trail.name}</h4>
            <div className="extra content">
              <span>
                <strong> Difficulty: {props.trail.difficulty} </strong>
              </span>
          </div>
        </div>
        <div className="content" >
          <div className="header"><strong>Location: </strong> {props.trail.location}</div>
        </div>
        <div>
          <span>
            <strong>Rating: </strong>
              {props.trail.stars} <Rating icon='star' defaultRating={5} />
          </span>
        </div>
        </div>
      </div>
      <Button color="red" center >More details</Button>
    </Card>
  );
};

export default TrailCard;
